import { useState, useEffect } from 'react';
import { getSchedules, createSchedule, updateSchedule, deleteSchedule } from '../api/schedules';

const useSchedule = () => {
    const [schedules, setSchedules] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchSchedules = async () => {
            try {
                const data = await getSchedules();
                setSchedules(data);
            } catch (err) {
                setError(err);
            } finally {
                setLoading(false);
            }
        };

        fetchSchedules();
    }, []);

    const addSchedule = async (schedule) => {
        try {
            const newSchedule = await createSchedule(schedule);
            setSchedules((prevSchedules) => [...prevSchedules, newSchedule]);
        } catch (err) {
            setError(err);
        }
    };

    const editSchedule = async (id, updatedSchedule) => {
        try {
            const schedule = await updateSchedule(id, updatedSchedule);
            setSchedules((prevSchedules) =>
                prevSchedules.map((s) => (s.id === id ? schedule : s))
            );
        } catch (err) {
            setError(err);
        }
    };

    const removeSchedule = async (id) => {
        try {
            await deleteSchedule(id);
            setSchedules((prevSchedules) => prevSchedules.filter((s) => s.id !== id));
        } catch (err) {
            setError(err);
        }
    };

    return {
        schedules,
        loading,
        error,
        addSchedule,
        editSchedule,
        removeSchedule,
    };
};

export default useSchedule;
