import React from 'react';
import { useSchedule } from '../../hooks/useSchedule';

const TodaySchedule = () => {
    const { schedule } = useSchedule();
    const today = new Date().toLocaleDateString('fr-FR', { weekday: 'long' });

    const todayClasses = schedule.filter(item => item.day === today);

    return (
        <div>
            <h2>Emploi du temps pour aujourd'hui</h2>
            {todayClasses.length > 0 ? (
                <ul>
                    {todayClasses.map((classItem, index) => (
                        <li key={index}>
                            {classItem.startTime} - {classItem.endTime}: {classItem.subject} ({classItem.className})
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Aucun cours pr√©vu pour aujourd'hui.</p>
            )}
        </div>
    );
};

export default TodaySchedule;
