import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api/documents';

// Function to upload a document
export const uploadDocument = async (formData) => {
    try {
        const response = await axios.post(`${API_URL}/upload`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        });
        return response.data;
    } catch (error) {
        throw new Error('Error uploading document: ' + error.message);
    }
};

// Function to get all documents
export const getDocuments = async () => {
    try {
        const response = await axios.get(API_URL);
        return response.data;
    } catch (error) {
        throw new Error('Error fetching documents: ' + error.message);
    }
};

// Function to delete a document
export const deleteDocument = async (documentId) => {
    try {
        const response = await axios.delete(`${API_URL}/${documentId}`);
        return response.data;
    } catch (error) {
        throw new Error('Error deleting document: ' + error.message);
    }
};

// Function to search documents
export const searchDocuments = async (query) => {
    try {
        const response = await axios.get(`${API_URL}/search`, { params: { q: query } });
        return response.data;
    } catch (error) {
        throw new Error('Error searching documents: ' + error.message);
    }
};
